"use strict";
console.log('running');
$(function () {
  var e;
  0 < $('input[name="check_cloud_version"]').length &&
    ((e = $('input[name="check_cloud_version"]').val()),
    $.post(e, { name: "scoreshop" }, function (e) {
      1 == e.data.upgrade &&
        new $.zui.Messager("云端有新版本可升级", {
          placement: "bottom-right",
          type: "danger",
          close: !0,
          icon: "ok-sign",
          time: 0,
          actions: [
            {
              name: "upgrade",
              icon: "cloud-upload",
              text: "立即升级",
              action: function () {
                1 == e.data.expired
                  ? new $.zui.Messager("The authorization has expired", {
                      type: "default",
                      placement: "bottom-right",
                      icon: "info-sign",
                      time: 1e3,
                    }).show()
                  : (window.location =
                      "/admin/Update/index?app_name=scoreshop");
              },
            },
          ],
        }).show();
    }));
});
